template.query:
const sale_details_data = [];
const update_items_data = [];
const item_ids = [];

new_item_details.data.map((item) => {
  sale_details_data.push({
    price: item.item_price,
    qty: item.item_qty,
    sale_id: createSale.data.result[0].id,
    item_id: item.item_id
  });
  update_items_data.push({
    id: item.item_id,
    qty: item.item_old_qty - item.item_qty
  });
  item_ids.push(item.item_id);
})

return {
  sale_details_data,
  update_items_data,
  item_ids
};

